export const initialState = {
    letter: {
        title: '',
        body: ''
    }
};
    export default (state = initialState, action) => {
      switch (action.type) {
        case 'ADD_LETTERTITLE':
          return { ...state, letter: {
              ...state.letter.body,
              title: action.lettertitle
            } };
        case 'ADD_LETTERBODY':
            return { ...state, letter: {
                ...state.letter.title,
                body: action.letterbody
                } };
        default:
          return state;
      }
    };
