export const initialState = {
  letter: {
      title: '',
      body: '',
      img: [],
  }, 
  list: {
    name: '',
  },
  openpage: {
    letter: true,
    list: false,
    menual: false
  }

};
    export default (state = initialState, action) => {
      switch (action.type) {
        case 'ADD_LETTER':
          return { ...state, letter: action.letter };
        case 'OPEN_WRITEMAIL':
          return { ...state, openpage: action.openpage };
        case 'OPEN_MAILLIST':
          return { ...state, openpage: action.openpage };
        case 'OPEN_USEMENUAL':
          return { ...state, openpage: action.openpage };
        default:
          return state;
      }
    };
