import React from 'react';
import { WriteMail } from '../interface'
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import MailIcon from '@material-ui/icons/Mail';
import MenuBookIcon from '@material-ui/icons/MenuBook';
import ListAltIcon from '@material-ui/icons/ListAlt';
import Divider from '@material-ui/core/Divider';
import CloseIcon from '@material-ui/icons/Close';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles(theme => ({
    paper: {
        padding: theme.spacing(3, 2),
        margin: theme.spacing(3)
    },
    appbar: {
      flexGrow: 1,
      backgroundColor: '#ffeb3b'
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
    },
    lettertitle: {
        marginTop: 12,
        padding: 6
    }
  }));

    function TabPanel(props) {
        const { children, value, index, ...other } = props;

        return (
        <Typography
            component="div"
            role="tabpanel"
            hidden={value !== index}
            id={`vertical-tabpanel-${index}`}
            aria-labelledby={`vertical-tab-${index}`}
            {...other}
        >
            <Box p={3}>{children}</Box>
        </Typography>
        );
    }

  TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
  };

  function a11yProps(index) {
    return {
      id: `vertical-tab-${index}`,
      'aria-controls': `vertical-tabpanel-${index}`,
    };
  }

export default function FirstPage() {
    const classes = useStyles();
    const [leftbar, setLeftBar] = React.useState(false);
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
      setValue(newValue);
    };

    const toggleDrawer = (open) => event => {
        if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
          return;
        }
        setLeftBar(open);
    };

    const handleDrawerClose = () => {
        setLeftBar(false);
    }

    const sideList = () => (
        <div
          className={classes.list}
          role="presentation"
          onClick={toggleDrawer(false)}
          onKeyDown={toggleDrawer(false)}
        >
          <List>
                <ListItem>
                    <Typography variant="h6" className={classes.title}>
                        메뉴
                    </Typography>
                    <IconButton onClick={handleDrawerClose}>
                        <CloseIcon />
                    </IconButton>
                </ListItem>
                <Divider/>
                <ListItem button value='0' onClick={handleChange}>
                    <ListItemIcon><MailIcon /></ListItemIcon>
                    <Typography variant="body1" className={classes.title}>
                        편지 쓰기
                    </Typography>
                </ListItem>
                <Divider/>
                <ListItem button value='1' onClick={handleChange}>
                <ListItemIcon><ListAltIcon /></ListItemIcon>
                    <Typography variant="body1" className={classes.title}>
                        편지 목록
                    </Typography>
                </ListItem>
                <Divider/>
                <ListItem button value='2' onClick={handleChange}>
                <ListItemIcon><MenuBookIcon /></ListItemIcon>
                    <Typography variant="body1" className={classes.title}>
                        사용 방법
                    </Typography>
              </ListItem>
              <Divider/>
          </List>
        </div>
    );

    return (
        <div>
            <AppBar position="sticky"  className={classes.appbar}>
                <Toolbar >
                    <IconButton edge="start" color="inherit" aria-label="menu" onClick={toggleDrawer(true)}>
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" className={classes.title}>
                        TimeWeb
                    </Typography>
                    <Button color="inherit">Login</Button>
                </Toolbar>
            </AppBar>
            <SwipeableDrawer
                open={leftbar}
                onClose={toggleDrawer(false)}
                onOpen={toggleDrawer(true)}
            >
                {sideList()}
            </SwipeableDrawer>
            <WriteMail />
        </div>
    );
}
