import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useDropzone } from 'react-dropzone';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import MailIcon from '@material-ui/icons/Mail';
import MenuBookIcon from '@material-ui/icons/MenuBook';
import ListAltIcon from '@material-ui/icons/ListAlt';
import Divider from '@material-ui/core/Divider';
import CloseIcon from '@material-ui/icons/Close';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import CreateIcon from '@material-ui/icons/Create';
import PlusImg from '../components/img/thumbnail.png'
import WriteMail from './interface'
const useStyles = makeStyles(theme => ({
    paper: {
        padding: theme.spacing(3, 2),
        margin: theme.spacing(3)
    },
    appbar: {
      flexGrow: 1,
      backgroundColor: '#ffeb3b'
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
    },
    lettertitle: {
        marginTop: 12,
        padding: 6
    }
  }));

  const thumbsContainer = {
    display: 'flex',
    flexDirection: 'row',
    flexWrap: 'wrap',
  };

export default function FirstPage() {
    const classes = useStyles();
    const dispatch = useDispatch();
    const [leftbar, setLeftBar] = React.useState(false);
    const letter = useSelector(state => state.rootReducer.letter, []);
    const [files, setFiles] = React.useState([]);

    useEffect(() => () => {
        // Make sure to revoke the data uris to avoid memory leaks
        files.forEach((file) => URL.revokeObjectURL(file.preview));
      }, [files]);

      const { getRootProps, getInputProps } = useDropzone({
        accept: 'image/*',
        onDrop: (acceptedFiles) => {
          setFiles(acceptedFiles.map((file) => Object.assign(file, {
            preview: URL.createObjectURL(file),
          })));
          dispatch({ type: 'ADD_LETTER', letter:{ ...letter, img: acceptedFiles }})
        },
      });

      const thumbs = files.map((file) => (
        <div key={file.name}>
          <div>
            <img
              src={file.preview}
              alt=""
              heigth="100%"
              width="100%"
            />
          </div>
        </div>
      ));

    const toggleDrawer = (open) => event => {
        if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
          return;
        }
        setLeftBar(open);
    };

    const handleDrawerClose = () => {
        setLeftBar(false);
    }

    const handleWriting = e => {
        dispatch({ type: 'ADD_LETTER', letter:{ ...letter, [e.target.id]: e.target.value }})
    }

    const sideList = () => (
        <div
          className={classes.list}
          role="presentation"
          onClick={toggleDrawer(false)}
          onKeyDown={toggleDrawer(false)}
        >
          <List>
                <ListItem>
                    <Typography variant="h6" className={classes.title}>
                        메뉴
                    </Typography>
                    <IconButton onClick={handleDrawerClose}>
                        <CloseIcon />
                    </IconButton>
                </ListItem>
                <Divider/>
                <ListItem button>
                    <ListItemIcon><MailIcon /></ListItemIcon>
                    <Typography variant="body1" className={classes.title}>
                        편지 쓰기
                    </Typography>
                </ListItem>
                <Divider/>
                <ListItem button>
                <ListItemIcon><ListAltIcon /></ListItemIcon>
                    <Typography variant="body1" className={classes.title}>
                        편지 목록
                    </Typography>
                </ListItem>
                <Divider/>
                <ListItem button>
                <ListItemIcon><MenuBookIcon /></ListItemIcon>
                    <Typography variant="body1" className={classes.title}>
                        사용 방법
                    </Typography>
              </ListItem>
              <Divider/>
          </List>
        </div>
    );

    return (
        <div>
            <AppBar position="sticky"  className={classes.appbar}>
                <Toolbar >
                    <IconButton edge="start" color="inherit" aria-label="menu" onClick={toggleDrawer(true)}>
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" className={classes.title}>
                        TimeWeb
                    </Typography>
                    <Button color="inherit">Login</Button>
                </Toolbar>
            </AppBar>
            <SwipeableDrawer
                open={leftbar}
                onClose={toggleDrawer(false)}
                onOpen={toggleDrawer(true)}
            >
                {sideList()}
            </SwipeableDrawer>
            <WriteMail />
        </div>
    );
}
